mapper.select:
select u.user_id, u.com_id, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, u.admin_flag, u.super_admin_flag, d.dept_id, d.parent_id, d.dept_name, d.leader, d.status as dept_status, c.id as com_id, c.company_name, c.com_code, c.active_flag, c.temp_id, c.active_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_company c on u.com_id = c.id where u.del_flag = '0'
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))
AND u.com_id = #{comId}
AND u.admin_flag = #{adminFlag}
AND u.super_admin_flag = #{superAdminFlag}
AND #{queryRoleId} IN (select r.role_id from sys_role r left join sys_user_role ur on ur.role_id = r.role_id where ur.user_id = u.user_id)
${params.dataScope}

select count(1) from sys_user


		and com_id = #{comId}


		and user_name = #{userName}

	and del_flag = 0

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, admin_flag = #{adminFlag}, super_admin_flag = #{superAdminFlag}, update_time = sysdate()
where user_id = #{userId}

mapper.select:
		select com_id
        from sys_user
        where phonenumber = #{phonenumber}
